function IceTab($,conf){var defconf={selector:'.ice-tab',ischild:true,tabs:'.j-tab',chooseClass:'choose',choose:0,mains:'.j-main',success:null};conf=$.extend(defconf,conf);$(conf.selector).each(function(){var $_this=$(this);var ischild=conf.ischild;var mains=conf.mains;var tabs=conf.tabs;var choose=conf.choose;var chooseClass=conf.chooseClass;var $tabs=$_this.find(tabs);var $mains;if(ischild){$mains=$_this.find(mains)}else{$mains=$(mains)};if($tabs.length!=$mains.length)return;$tabs.eq(choose).addClass(chooseClass);$mains.hide();$mains.eq(choose).show();$tabs.each(function(idx){$(this).on('click',function(){if(conf.success){try{conf.success()}catch(e){}};$tabs.removeClass(chooseClass);$(this).addClass(chooseClass);$mains.hide();$mains.eq(idx).show()})})})};